import React, { useEffect, useState } from "react";
import { Form, Grid } from "semantic-ui-react";

import { useSubstrate } from "./substrate-lib";
import { TxButton } from "./substrate-lib/components";

import KittyCards from "./KittyCards";

export default function Kitties(props) {
  const { api, keyring } = useSubstrate();
  const { accountPair } = props;

  const [kitties, setKitties] = useState([]);
  const [status, setStatus] = useState("");

  const [kittyDNAs, setKittyDNAs] = useState([]);
  const [kittyOwners, setKittyOwners] = useState([]);

  const fetchKitties = () => {
    // TODO: 在这里调用 `api.query.kittiesModule.*` 函数去取得猫咪的信息。
    // 你需要取得：
    //   - 共有多少只猫咪
    //   - 每只猫咪的主人是谁
    //   - 每只猫咪的 DNA 是什么，用来组合出它的形态

    let unsubDNAArray = null;
    let unsubOwnerArray = null;

    const asyncFetch = async () => {
      const count = await api.query.kittiesModule.kittiesCount();

      const indexArray = Array.from({ length: count }, (v, i) => i);

      unsubDNAArray = await api.query.kittiesModule.kitties.multi(
        indexArray,
        (dnaArray) => setKittyDNAs(dnaArray)
      );

      unsubOwnerArray = await api.query.kittiesModule.owner.multi(
        indexArray,
        (ownerArray) => setKittyOwners(ownerArray)
      );
    };

    asyncFetch();

    // return () => {
    //   unsubDNAArray && unsubDNAArray();
    //   unsubOwnerArray && unsubOwnerArray();
    // };
  };

  const populateKitties = () => {
    // TODO: 在这里添加额外的逻辑。你需要组成这样的数组结构：
    //  ```javascript
    //  const kitties = [{
    //    id: 0,
    //    dna: ...,
    //    owner: ...
    //  }, { id: ..., dna: ..., owner: ... }]
    //  ```
    // 这个 kitties 会传入 <KittyCards/> 然后对每只猫咪进行处理

    const kitties = [];

    console.log(`DNAs: ${kittyDNAs}`);
    console.log(`Owners: ${kittyOwners}`);
    for (let i = 0, len = kittyOwners.length; i < len; ++i) {
      kitties[i] = {};
      kitties[i].id = i;
      kitties[i].dna = kittyDNAs[i].unwrapOrDefault();
      kitties[i].owner = keyring.encodeAddress(kittyOwners[i].unwrap());
      console.log(
        `item: ${kitties[i].id}, ${kitties[i].dna}, ${kitties[i].owner}`
      );
    }
    console.log(`kitties Array: ${kitties}`);
    setKitties(kitties);
  };

  useEffect(fetchKitties, [api, keyring]);
  useEffect(populateKitties, [kittyOwners]);

  return (
    <Grid.Column width={16}>
      <h1>小毛孩</h1>
      <KittyCards
        kitties={kitties}
        accountPair={accountPair}
        setStatus={setStatus}
      />
      <Form style={{ margin: "1em 0" }}>
        <Form.Field style={{ textAlign: "center" }}>
          <TxButton
            accountPair={accountPair}
            label="创建小毛孩"
            type="SIGNED-TX"
            setStatus={setStatus}
            attrs={{
              palletRpc: "kittiesModule",
              callable: "create",
              inputParams: [],
              paramFields: [],
            }}
          />
        </Form.Field>
      </Form>
      <div style={{ overflowWrap: "break-word" }}>{status}</div>
    </Grid.Column>
  );
}
